INSERT INTO after_the_trip (id, after_trip_text)
  VALUES (36, 'Хотя некоторые болезни могут начаться во время путешествия, другие могут появляться в течение нескольких недель, месяцев или даже лет после возвращения. История путешествия, особенно в течение предыдущих 6 месяцев, должна быть частью обычной истории болезни для каждого больного. <br/>Малярия. Любой пациент с лихорадкой после поездки в зону риска малярии в течение последнего года должен быть немедленно обследован с использованием соответствующих диагностических тестов на малярию. Малярия, особенно P. falciparum , требует срочного вмешательства, поскольку клиническое ухудшение может происходить быстро и непредсказуемо.<br/><br/>');

